import React, { useState } from 'react';

function TodoApp() {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  function TaskButton() {
    const [showWizard, setShowWizard] = useState(false);
    const [taskName, setTaskName] = useState('');
    const [taskDescription, setTaskDescription] = useState('');

    // Function to handle button click and show the wizard
    const handleButtonClick = () => {
      setShowWizard(true);
    };

    // Function to handle form submission in the wizard
    const handleFormSubmit = (e) => {
      e.preventDefault();
      // Perform any additional processing or API calls with the task name and description

      // Reset the form and hide the wizard
      setTaskName('');
      setTaskDescription('');
      setShowWizard(false);
    };

    return (
      <div>
        <button onClick={handleButtonClick}>Create Detail Task</button>
        {showWizard && (
          <div>
            <h2>Create Task</h2>
            <form onSubmit={handleFormSubmit}>
              <label>
                Task Name:
                <input
                  type="text"
                  value={taskName}
                  onChange={(e) => setTaskName(e.target.value)}
                />
              </label>
              <br />
              <label>
                Task Description:
                <textarea
                  value={taskDescription}
                  onChange={(e) => setTaskDescription(e.target.value)}
                />
              </label>
              <br />
              <button type="submit">Save</button>
            </form>
          </div>
        )}
      </div>
    );
  }

  const handleAddTodo = () => {
    if (inputValue.trim() !== '') {
      setTodos([...todos, inputValue]);
      setInputValue('');
    }
  };

  const handleDeleteTodo = (index) => {
    const updatedTodos = todos.filter((_, i) => i !== index);
    setTodos(updatedTodos);
  };

  return (
    <div>
      <h2>Todo</h2>
      <div>
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder="Enter a task"
        />
        <button onClick={handleAddTodo}>Create task</button>
      </div>
      <ul>
        {todos.map((todo, index) => (
          <li key={index}>
            {todo}
            <button onClick={() => handleDeleteTodo(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TodoApp;
