import React, { useState } from 'react';
import  loginDetails from '../Login.json';

const Login=()=> {
  let navigate=useNavigate();

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
    console.log(setUsername)
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
    console.log(setPassword)
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if(loginDetails.email===username && loginDetails.password===password){
      navigate("/todolist",true)
    }else{

    }
    // Add your login logic here, such as sending a request to the server for authentication
    // You can use username and password values to make the request
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div class="logindiv">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={handleUsernameChange}
          />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={handlePasswordChange}
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
}

export default Login;
